package zhonghcc.P0005_LongestPalindromicSubstring;

import junit.framework.Assert;
import org.junit.Test;

/**
 * Created by zhong on 2017/2/6.
 */
public class Solution2Test {

    @Test
    public void testLongestPalindrome() throws Exception {

        Solution2 s = new Solution2();
        Assert.assertEquals("bab",s.longestPalindrome("babad"));
        Assert.assertEquals("bb",s.longestPalindrome("cbbd"));
        Assert.assertEquals("a",s.longestPalindrome("abcd"));
        Assert.assertEquals("",s.longestPalindrome(""));
        Assert.assertEquals("abbaabba",s.longestPalindrome("ababbaabbaab"));
        Assert.assertEquals("",
                s.longestPalindrome(""));
    }


}